import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { useSelector } from 'react-redux';
import ProfileImageWithDefault from './ProfileImageWithDefault';
import { createActivity } from '../api/apiCalls';

const ActivitySubmit = () => {
    const { image } = useSelector((store) => ({ image: store.image }));
    const [focused, setFocused] = useState(false);
    const [activity, setActivity] = useState('')
    const { t } = useTranslation();

    useEffect(() => {
        if (!focused) {
            setActivity('')
        }
    }, [focused])

    const onClickEvent = async () => {
        const body = {
            content: activity
        }
        try {
            await createActivity(body);
        } catch (error) {

        }
    }

    return (
        <div className="card p-1 flex-row">
            <ProfileImageWithDefault image={image} width="32" height="32" className="rounded-circle" />
            <div className="flex-fill">
                <textarea className="form-control"
                    rows={focused ? '3' : '1'}
                    onFocus={() => setFocused(true)}
                    onChange={(event) => setActivity(event.target.value)}
                    value={activity}
                />
                {focused && (<div className="text-right mt-1">
                    <button className="btn btn-primary" onClick={onClickEvent}>Create Activity</button>
                    <button
                        className="btn btn-light d-inline-flex ml-1"
                        onClick={() => setFocused(false)}
                    >
                        <i className="material-icons">close</i>
                        {t('Cancel')}
                    </button>
                </div>)}
            </div>
        </div>
    );
};

export default ActivitySubmit;
