import { createStore, applyMiddleware, compose } from 'redux';
import authReducer from './authReducer';
import SecureLS from 'secure-ls';
import thunk from 'redux-thunk';


const secureLs = new SecureLS();

const getStateFromStorage = () => {
    const appAuth = secureLs.get('app-auth');

    let stateInLocalStorage = {
        isLoggedIn: false,
        username: undefined,
        displayName: undefined,
        image: undefined,
        password: undefined
    }

    if (appAuth) {
        return stateInLocalStorage = appAuth;
    }
    return stateInLocalStorage;
};

const updateStateInStorage = newState => {
    secureLs.set('app-auth', newState);

};

const configureStore = () => {
    const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
    // window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
    const store = createStore(authReducer, getStateFromStorage(), composeEnhancers(applyMiddleware(thunk)));

    store.subscribe(() => {//store'da her hangi bir değişimde arkadak çalışacak
        updateStateInStorage(store.getState());
    });

    return store;
}

export default configureStore;
