import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { getActivities } from '../api/apiCalls';


const Activities = () => {
    const [activityPage, setActivityPage] = useState({ content: [] });
    const { t } = useTranslation();

    useEffect(() => {
        const loadActivities = async () => {
            try {
                const response = await getActivities();
                setActivityPage(response.data)
            } catch (error) { }
        };
        loadActivities();
    }, []);

    const { content } = activityPage;

    if (content.length === 0) {
        return <div className="alert alert-secondary text-center">
            {t('There are no activities')}
        </div>
    }
    {
        content.map(activity => {
            return (
                <div className="card mb-3">



                    <div className="row no-gutters">
                        <div className="col-md-4">
                            <img className="img-fluid" src="http://placehold.it/250x350" alt="Card image cap" />
                        </div>
                        <div className="col-md-8">
                            <div className="card-body">
                                <h5 className="card-title"> Tehapy Tanıtım</h5>
                                <p className="card-text">Tehapy projesinin sunumu</p>
                                <a href="#" className="btn btn-primary">Daha Fazla Oku... &rarr;</a>

                            </div>

                        </div>

                    </div>
                    <div className="card-footer text-muted text-right">
                        25.11.2020 tarihinde<a href="#" className="text-decoration-none"> Veli Adin </a> tarafından oluşturuldu
            </div>
                </div>
            );
        })
    }

};

export default Activities;
