import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import ProfileImageWithDefault from './ProfileImageWithDefault';
import { useTranslation } from "react-i18next";
import Input from "../components/Input";


const ProfileCard = (props) => {
  const { username: loggedInUsername } = useSelector((store) => {
    return {
      username: store.username,
    };
  });
  const [inEditMode, setInEditMode] = useState(false);
  const [updatedDisplayName, setUpdatedDisplayName] = useState();


  const onClickEdit = () => {
    console.log(updatedDisplayName);
  }

  const { t } = useTranslation();
  const { user } = props;
  const { username, displayName, image } = user;

  const routeParams = useParams();

  const pathUsername = routeParams.username;
  let message = "We cannot edit";
  if (pathUsername === loggedInUsername) {
    message = "We can edit";
  }

  return (
    <div className="card text-center">
      <div className="card-header">
        <ProfileImageWithDefault
          className="rounded-circle shadow"
          width="150"
          height="160"
          alt={`${username} profile`}
          image={image}
        />
      </div>
      <div className="card-body">
        {(!inEditMode && <>
          <h4>
            {displayName}
            {username}
          </h4>

          <button className="btn btn-success  d-inline-flex" onClick={() => setInEditMode(true)}><span className="material-icons">create</span>
            {t('Edit')}</button></>
        )}
        {(inEditMode && (
          <div>
            <Input label={t('Change Display Name')} defaultValue={displayName} onChange={(event) =>{setUpdatedDisplayName(event.target.value)}}></Input>
            <div>
              <button className="btn btn-primary d-inline-flex"><span class="material-icons">
                save
              </span>
                {t('Save')}
            </button>
              <button className="btn" onClick={()=> setInEditMode(false)}>{t('Cancel')}</button>
            </div>
          </div>
        ))}


      </div>

    </div>
  );
};

export default ProfileCard;
