import React from 'react';
import icon from '../assets/icon-light.png';

import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { useDispatch, useSelector } from 'react-redux';
import { logoutSucces } from '../redux/authActions';
import ProfileImageWithDefault from './ProfileImageWithDefault';

const TopBar = (props) => {

    const { t } = useTranslation();

    const { username, isLoggedIn, displayName, image } = useSelector((store) => { //reduxState'ten alÄ±yoruz gibi...
        return {
            isLoggedIn: store.isLoggedIn,
            username: store.username,
            displayName: store.displayName,
            image: store.image,
        }
    })

    const dispatch = useDispatch();

    const onLogoutSuccess = () => {
        dispatch(logoutSucces());
    }


    let links = (
        <div className="collapse navbar-collapse">
            <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                    <Link className="nav-link"
                        to="/">{t('Events')}</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link"
                        to="/login">{t('Login')}</Link>
                </li>
                <li class="nav-item">
                    <Link className="nav-link"
                        to="/signup">{t('Sign Up')}</Link>
                </li>
            </ul>
        </div>


    );
    if (isLoggedIn) {
        links = (
            <div className="collapse navbar-collapse">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        <Link className="nav-link"
                            to="/">{t('Events')}</Link>
                    </li>
                    <li className="nav-item dropdown">
                        <div className="d-flex" style={{ cursor: 'pointer' }}>
                            <ProfileImageWithDefault image={image} width="32" height="32" className="rounded-circle m-auto" />
                            <span className="nav-link dropdown-toggle">
                                {displayName}
                            </span>
                        </div>
                        <div className="dropdown-menu show" style={{ cursor: 'pointer', color: 'black' }}>
                            <Link className="nav-link dropdown-item" to={'/user/' + username}>
                                {t('My Profile')}
                            </Link>
                            <span className="nav-link dropdown-item" onClick={onLogoutSuccess} style={{ cursor: 'pointer' }}>
                                {t('Logout')}
                            </span>
                        </div>
                    </li>


                </ul>
            </div >
        )
    }
    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
            <div className="container">
                <Link className="navbar-brand" to="/">
                    <img src={icon} width="50" alt="icon" /> TEHAPY
                </Link>

                {links}
            </div>
        </nav>
    );
}


export default TopBar;
