import React from 'react';
import logo from '../assets/logo.png';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { useDispatch, useSelector } from 'react-redux';
import { logoutSucces } from '../redux/authActions';

const TopBar = (props) => {

    const { t } = useTranslation();

    const { username, isLoggedIn } = useSelector((store) => { //reduxState'ten alıyoruz gibi...
        return {
            isLoggedIn: store.isLoggedIn,
            username: store.username
        }
    })

    const dispatch = useDispatch();

    const onLogoutSuccess = () => {
        dispatch(logoutSucces());
    }


    let links = (
        <div className="collapse navbar-collapse">
            <ul className="navbar-nav ml-auto">
                <li className="nav-item">
                    <Link className="nav-link"
                        to="/login">{t('Login')}</Link>
                </li>
                <li class="nav-item">
                    <Link className="nav-link"
                        to="/signup">{t('Sign Up')}</Link>
                </li>
            </ul>
        </div>


    );
    if (isLoggedIn) {
        links = (
            <div className="collapse navbar-collapse">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        <Link className="nav-link" to={'/user/' + username}>
                            {username}
                        </Link>
                    </li>
                    <li className="nav-link" onClick={onLogoutSuccess} style={{ cursor: 'pointer' }}>
                        {t('Logout')}
                    </li>
                </ul>
            </div>
        )
    }
    return (
        <div className="shadow-sm bg-light mb-2">
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
                <Link className="navbar-brand" to="/">
                    <img src={logo} width="60" alt="Logo" /> Uygulama Adı
                    </Link>
                {links}
            </nav>
        </div>
    );
}


export default TopBar;
