import React from 'react';
import Input from '../components/input'
import { withTranslation } from 'react-i18next'
import { login } from '../api/apiCalls'

class UserLoginPage extends React.Component {
    state = {
        username: null,
        password: null,
        error: null
    };

    onChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }
    onClickLogin = async event => {
        event.preventDefault();
        const { username, password } = this.state;
        const creds = {
            username,
            password
        };
        try {
            await login(creds)
        } catch (apiError) {
            this.setState({
                error: apiError.response.data.message
            })
        }
    };

    render() {
        const { t } = this.props;
        return (
            <div className="container">
                <form>
                    <h1 className="text-center">{t('Login')}</h1>
                    <div className="form-group">
                        <Input name="username" label={t('Username')} onChange={this.onChange} />
                        <Input name="password" label={t('Password')} type="password" onChange={this.onChange} />
                        {this.state.error && <div class="alert alert-danger" role="alert">{this.state.error}</div>}
                        <div className="text-center">
                            <button type="submit" className="btn btn-primary" onClick={this.onClickLogin} >
                                {<span className="spinner-border spinner-border-sm" ></span>}{t('Login')}
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default withTranslation()(UserLoginPage);
