import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { getActivities } from '../api/apiCalls';
import ActivityView from './ActivityView';

const Activities = () => {
    const [activityPage, setActivityPage] = useState({ content: [], last: true, number: 0 });
    const { t } = useTranslation();

    useEffect(() => {
        const loadActivities = async () => {
            try {
                const response = await getActivities();
                setActivityPage(response.data)
            } catch (error) { }
        };
        loadActivities();
    }, []);


    const loadActivities = async (page) => {
        try {
            const response = await getActivities(page);
            setActivityPage(previousActivityPage => ({
                ...response.data,
                content: [...previousActivityPage.content, ...response.data.content]
            }))
        } catch (error) { }
    };

    const { content, last, number } = activityPage;

    if (content.length === 0) {
        return <div className="alert alert-secondary text-center">
            {t('There are no activities')}
        </div>
    }

    return (
        <div>
            {content.map(activity => {
                return <ActivityView key={activity.id} activity={activity}></ActivityView>
            })}
            {!last && <div className="alert alert-secondary text-center"
                onClick={() => loadActivities(number + 1)}
                style={{ cursor: 'pointer' }}
            >
                {t('Load new activities')}
            </div>}
        </div>
    );
};

export default Activities;
