import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { getActivities } from '../api/apiCalls';
import { useApiProgress } from '../shared/ApiProgress';
import ActivityView from './ActivityView';
import Spinner from '../components/Spinner';

const Activities = () => {

    const [page, setPage] = useState({
        content: [],
        size: 2,
        number: 0
    });

    const [activityPage, setActivityPage] = useState({ content: [], last: true, number: 0 });
    const { t } = useTranslation();

    const pendingApiCall = useApiProgress('get', '/api/getActivities?page=');

    useEffect(() => {
        loadActivities();
    }, []);

    const loadActivities = async (page) => {
        try {
            const response = await getActivities(page);
            setActivityPage(previousActivityPage => ({
                ...response.data,
                content: [...previousActivityPage.content, ...response.data.content]
            }))
        } catch (error) { }
    };

    const { content, last, number } = activityPage;

    if (content.length === 0) {
        return <div className="alert alert-secondary text-center">
            {pendingApiCall ? <Spinner /> : t('There are no activities')}
        </div>
    }

    return (
        <div>
            {content.map(activity => {
                return <ActivityView key={activity.id} activity={activity}></ActivityView>
            })}
            {!last && <div className="alert alert-secondary text-center"
                onClick={pendingApiCall ? () => { } : () => loadActivities(number + 1)}
                style={{ cursor: pendingApiCall ? 'not-allowed' : 'pointer' }}
            >
                {pendingApiCall ? <Spinner /> : t('Load new activities')}
            </div>}
        </div>
    );
};

export default Activities;
