import React from 'react';
import Input from '../components/input'
import { withTranslation } from 'react-i18next'

class UserLoginPage extends React.Component {
    state = {
        username: null,
        password: null,
        pendingApiCall: false,
        errors: {}
    };

    onChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name] : value
        })
    }

    render() {
        const { t } = this.props;
        return (
            <div className="container">
                <form>
                    <h1 className="text-center">Giriş Yap</h1>
                    <div className="form-group">
                        <Input name="username" label="Username" onChange={this.onChange} />
                        <Input name="password" label="Password" type="password" onChange={this.onChange} />
                        <div className="text-center">
                            <button type="submit" className="btn btn-primary" >
                                {<span className="spinner-border spinner-border-sm" ></span>}Giriş Yap
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default UserLoginPage
