import React from 'react';
import Input from '../components/input'
import { withTranslation } from 'react-i18next'
import { login } from '../api/apiCalls'
import axios from 'axios';

class UserLoginPage extends React.Component {
    state = {
        username: null,
        password: null,
        error: null,
        pendingApiCall: false
    };

componentDidMount(){
    axios.interceptors.request.use((request) => {
        this.state({pendingApiCall : true});
        return request;
    })
    axios.interceptors.response.use((response) =>{
        this.state({pendingApiCall : false})
    },(error) => {
        this.setState({pendingApiCall : false});
        throw error;
    });
}

    onChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
            error:null
        })
    }
    onClickLogin = async event => {
        event.preventDefault();
        const { username, password } = this.state;
        const creds = {
            username,
            password
        };
        this.setState({
            error:null
        })
        try {
            await login(creds)
        } catch (apiError) {
            this.setState({
                error: apiError.response.data.message
            })
        }
    };

    render() {
        const { t } = this.props;
        const {username, password, error} = this.state;
        const buttonEnabled = username && password;
        return (
            <div className="container">
                <form>
                    <h1 className="text-center">{t('Login')}</h1>
                    <div className="form-group">
                        <Input name="username" label={t('Username')} onChange={this.onChange} />
                        <Input name="password" label={t('Password')} type="password" onChange={this.onChange} />
                        {error && <div class="alert alert-danger" role="alert">{error}</div>}
                        <div className="text-center">
                            <button type="submit" 
                            className="btn btn-primary" 
                            onClick={this.onClickLogin} disabled={!buttonEnabled || pendingApiCall}>
                                {<span className="spinner-border spinner-border-sm" ></span>}{t('Login')}
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default withTranslation()(UserLoginPage);
