import React, { Component } from 'react';
import { getUsers } from '../api/apiCalls';
import { withTranslation } from 'react-i18next';
import UserListItem from './UserListItem';
class UserList extends Component {

    state = {
        page: {
            content: [],
            size: 3,
            pageNumber: 0
        }
    }

    componentDidMount() {
        getUsers().then(response => {
            this.setState({
                page: response.data
            });
        });
    }

    onClickNext = () => {
        const nextPage = this.state.page.pageNumber + 1;
        getUsers(nextPage).then(response => {
            this.setState({
                page: response.data
            });
        });
    }

    render() {
        const { t } = this.props;
        const { content: users, last, first } = this.state.page;
        return (
            <div className="card">
                <h3 className="card-header text-center">{t('Users')}</h3>
                <div className="list-group-flush">
                    {users.map((user) => (
                        <UserListItem key={user.username} user={user} />
                    ))}
                </div>
                <div>
                    {last === false && <button className="btn btn-sm btn-light" onClick={this.onClickNext}>Next</button>}
                </div>
            </div>
        );
    }
}

export default withTranslation()(UserList);
