import React, { useState } from 'react';
import Input from '../components/input'
import { withTranslation } from 'react-i18next'
import ButtonWithProgress from '../components/buttonWithProgres';
import { withApiProgress } from '../shared/ApiProgress'
import { connect } from 'react-redux';
import { loginHandler } from '../redux/authActions';
//import { Authentication } from '../shared/AuthenticationContext'

const UserLoginPage = (props) => {

    //static contextType = Authentication;

    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [error, setError] = useState();
    /* state = {
         username: null,
         password: null,
         error: null
     };*/

    const onClickLogin = async event => {
        event.preventDefault();
        //const { username, password } = this.state;
        const creds = {
            username,
            password
        };

        const { history, dispatch } = props;
        const { push } = history;

        setError(undefined);
        try {
            await dispatch(loginHandler(creds));
            push('/')
        } catch (apiError) {
            setError(apiError.response.data.message);
        }
    };
    const { t, pendingApiCall } = props;
    const buttonEnabled = username && password;
    return (
        <div className="container">
            <form>
                <h1 className="text-center">{t('Login')}</h1>
                <div className="form-label-group">
                    <Input label={t('Username')} onChange={event => setUsername(event.target.value)} />
                    <Input label={t('Password')} type="password" onChange={event => setPassword(event.target.value)} />
                    {error && <div class="alert alert-danger" role="alert">{error}</div>}
                    <ButtonWithProgress
                        onClick={onClickLogin}
                        disabled={!buttonEnabled || pendingApiCall}
                        pendingApiCall={pendingApiCall}
                        text={t('Login')} />
                </div>
            </form>
        </div>
    );
}

const LoginPageWithTransation = withTranslation()(UserLoginPage);

/*const mapDispatchToProps = dispatch => {
    return {
                        onLoginSuccess: authState => {
            return dispatch(loginSuccess(authState));
        }
    };
};*/

export default connect()(withApiProgress(LoginPageWithTransation, '/api/auth'));
