import React, { useState, useEffect } from 'react';
import Input from '../components/input'
import { useTranslation } from 'react-i18next'
import ButtonWithProgress from '../components/buttonWithProgres';
import { useApiProgress } from '../shared/ApiProgress'
import { useDispatch } from 'react-redux';
import { loginHandler, loginWithSocialHandler } from '../redux/authActions';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';

const UserLoginPage = (props) => {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [error, setError] = useState();
    const [form, setForm] = useState({
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null,
    });
    
    const dispatch = useDispatch();

    useEffect(() => { // username veya password'da bir değişim olduğunda, errorlar undefined oluyor.
        setError(undefined);
    }, [username, password])

    const onClickLogin = async event => {
        event.preventDefault();
        const creds = {
            username,
            password
        };
        const { history } = props;
        const { push } = history;

        setError(undefined);
        try {
            await dispatch(loginHandler(creds));
            push('/')
        } catch (apiError) {
            setError(apiError.response.data.message);
        }
    };
    //Facebook Area S
    const componentClicked = data => {
        console.log("data", data);
    };

    const responseFacebook = response => {
        console.log(response);
    };
    //Facebook Area F Google S
    const responseGoogle = (response) => {
        console.log(response)


        if (response.accessToken) {
            const { history } = props;
            const { push } = history;
            setError(undefined);
            try {
                setUsername(response.profileObj.givenName);
                dispatch(loginWithSocialHandler(response.profileObj));
                push('/');
            } catch (error) {
                setError(error.response.data.message);
            }

        }
    };
    //Google F
    const { t } = useTranslation();
    const pendingApiCall = useApiProgress('/api/auth');
    const buttonEnabled = username && password;
    return (
        <div className="container">
            <form>
                <h1 className="text-center">{t('Login')}</h1>
                <div className="form-label-group">
                    <Input label={t('Username')} onChange={event => setUsername(event.target.value)} />
                    <Input label={t('Password')} type="password" onChange={event => setPassword(event.target.value)} />
                    {error && <div className="alert alert-danger" role="alert">{error}</div>}
                    <ButtonWithProgress
                        onClick={onClickLogin}
                        disabled={!buttonEnabled || pendingApiCall}
                        pendingApiCall={pendingApiCall}
                        text={t('Login')} />


                </div>
                <FacebookLogin
                    appId="2978237078953063"
                    fields="name,email,picture"
                    onClick={componentClicked}
                    callback={responseFacebook}
                />
                <GoogleLogin
                    clientId="181124231251-7hfpmban9e79jkgllfph3rnp0i8o6t27.apps.googleusercontent.com"
                    buttonText="Login"
                    onSuccess={responseGoogle}
                    onFailure={responseGoogle}
                    cookiePolicy={'single_host_origin'}
                />
            </form>


        </div>
    );
}


export default UserLoginPage;
