import React from 'react';
import Input from '../components/input'
import { withTranslation } from 'react-i18next'
import { login } from '../api/apiCalls'
import ButtonWithProgress from '../components/buttonWithProgres';
import { withApiProgress } from '../shared/ApiProgress'
import {connect } from 'react-redux';
//import { Authentication } from '../shared/AuthenticationContext'

class UserLoginPage extends React.Component {

    //static contextType = Authentication;
    state = {
        username: null,
        password: null,
        error: null
    };

    onChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
            error: null
        })
    }
    onClickLogin = async event => {
        event.preventDefault();
        const { username, password } = this.state;
        const creds = {
            username,
            password
        };
        const  onLoginSuccess = () => {};
        const { push } = this.props.history;

        this.setState({
            error: null
        })
        try {
            const response = await login(creds);
            push('/')

            
            const authState = {
                ...response.data,
                password : password
            }

            onLoginSuccess(authState);
        } catch (apiError) {
            this.setState({
                error: apiError.response.data.message
            })
        }
    };

    render() {
        const { t, pendingApiCall } = this.props;
        const { username, password, error } = this.state;
        const buttonEnabled = username && password;
        return (
            <div className="container">
                <form>
                    <h1 className="text-center">{t('Login')}</h1>
                    <div className="form-group">
                        <Input name="username" label={t('Username')} onChange={this.onChange} />
                        <Input name="password" label={t('Password')} type="password" onChange={this.onChange} />
                        {error && <div class="alert alert-danger" role="alert">{error}</div>}
                        <ButtonWithProgress
                            onClick={this.onClickLogin}
                            disabled={!buttonEnabled || pendingApiCall}
                            pendingApiCall={pendingApiCall}
                            text={t('Login')} />

                    </div>
                </form>
            </div>
        );
    }
}
const LoginPageWithTransation = withTranslation()(UserLoginPage);
export default connect(withApiProgress(LoginPageWithTransation, '/api/auth'));
