import React from 'react';
import axios from'axios';

class UserSignupPage extends React.Component {

    state = {
        username : null,
        displayName : null,
        password : null,
        passwordRepeat: null
    };

    onChange = event =>{
        const { name, value} = event.target;
        this.setState({
            [name]: value
        });
    };

    onClickSignup = event => {
        event.preventDefault();

        const {username, displayName,password} = this.state;
        const body = {
            username,
            displayName,
            password
        }

        axios.post('/api/createuser',body)
    }


    render() {
        return (
            <form>
                <h1>Sign Up</h1>

                <div>
                    <label>Username</label>
                    <input name="username" onChange={this.onChange}></input>
                </div>
                <div>
                    <label>Display Name</label>
                    <input name="displayName" onChange={this.onChange}></input>
                </div>
                <div>
                    <label>Password</label>
                    <input name="password" type="password" onChange={this.onChange}></input>
                </div>
                <div>
                    <label>Password Repeat</label>
                    <input name="passwordRepeat" type="password" onChange={this.onChange}></input>
                </div>
                <button onClick={this.onClickSignup}>Sign Up</button>

            </form>


        );
    }
}

export default UserSignupPage;
