import { createStore } from 'redux';
import authReducer from './authReducer';


const configureStore = () => {
    const appAuth = localStorage.getItem('app-auth');

    let stateInLocalStorage = {
        isLoggedIn: false,
        username: undefined,
        displayName: undefined,
        image: undefined,
        password: undefined
    }

    if (appAuth) {
        stateInLocalStorage = JSON.parse(appAuth)
    }

    const store = createStore(authReducer, stateInLocalStorage, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

    store.subscribe(() => {//store'da her hangi bir değişimde arkadak çalışacak
        localStorage.setItem('app-auth', JSON.stringify(store.getState()))
    })

    return store;
}

export default configureStore;
