import React from 'react';
import { useTranslation } from 'react-i18next';
import ButtonWithProgress from '../components/ButtonWithProgress';
const Modal = (props) => {
    const { visible, onClickCancel, message, onClickOk, pendingApiCall } = props;
    const { t } = useTranslation();
    let className = 'modal fade';
    if (visible) {
        className += ' show d-block'
    }


    return (
        <div className={className} style={{ backgroundColor: '#000000b0' }} >
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">{message}</h5>
                    </div>
                    <div className="modal-body">
                        <p>{t('Are you sure to delete activity?')}</p>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" onClick={onClickCancel} >{t('Cancel')}</button>
                        <ButtonWithProgress className="btn btn-danger" onClick={onClickOk} pendingApiCall={pendingApiCall} text={t('Delete Activity')} />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Modal;
