import React, { useContext } from 'react';
import logo from '../assets/logo.png';
import { Link } from 'react-router-dom';
import { withTranslation } from 'react-i18next';
//import { connect } from 'react-redux';
//import { logoutSucces } from '../redux/authActions';
import { Authentication } from '../shared/AuthenticationContext';

const TopBar = (props) => {

    //static contextType = Authentication;
    const context = useContext(Authentication);
    /*  onClickLogout = () => {
          this.props.dispatch(logoutSucces());
      }*/

    //const { t, username, isLoggedIn, onLogoutSuccess } = props;
    const { t } = props;
    const { onLogoutSuccess, state } = context;
    const { username, isLoggedIn } = state;

    let links = (
        <ul className="navbar-nav ml-auto">
            <li>
                <Link className="nav-link" to="/login">{t('Login')}</Link>
            </li>
            <li>
                <Link className="nav-link" to="/signup">{t('Sign Up')}</Link>
            </li>
        </ul>
    );
    if (isLoggedIn) {
        links = (
            <ul className="navbar-nav ml-auto">
                <li>
                    <Link className="nav-link" to={'/user/' + username}>
                        {username}
                    </Link>
                </li>
                <li className="nav-link" onClick={onLogoutSuccess} style={{ cursor: 'pointer' }}>
                    {t('Logout')}
                </li>
            </ul>
        )
    }
    return (
        <div className="shadow-sm bg-light mb-2">
            <nav className="navbar navbar-light container navbar-expand">
                <Link className="navbar-brand" to="/">
                    <img src={logo} width="60" alt="Logo" /> Uygulama AdÄ±
                    </Link>
                {links}
            </nav>
        </div>
    );
}
const TopBarWithTranslation = withTranslation()(TopBar);
/*
const mapStateToProps = (store) => {
    return {
        isLoggedIn: store.isLoggedIn,
        username: store.username
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onLogoutSuccess : () => {
            return dispatch(logoutSucces());
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TopBarWithTranslation);
*/
export default TopBarWithTranslation;
