import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import ProfileImageWithDefault from './ProfileImageWithDefault';
import { useTranslation } from "react-i18next";
import Input from "../components/Input";
import { updateUser } from "../api/apiCalls";
import { useApiProgress } from "../shared/ApiProgress";
import ButtonWithProgress from '../components/ButtonWithProgress';

const ProfileCard = (props) => {
  const { username: loggedInUsername } = useSelector((store) => {
    return {
      username: store.username,
    };
  });
  const [inEditMode, setInEditMode] = useState(false);
  const [updatedDisplayName, setUpdatedDisplayName] = useState();
  const [user, setUser] = useState({});
  const [editable, setEditable] = useState(false);
  const [newImage, setNewImage] = useState();

  const { username, displayName, image } = user;
  const { t } = useTranslation();
  //const { user } = props;
  const routeParams = useParams();
  const pathUsername = routeParams.username;

  useEffect(() => {
    setUser(props.user);
  }, [props.user]);

  useEffect(() => {
    setEditable(pathUsername === loggedInUsername)
  }, [pathUsername, loggedInUsername])

  useEffect(() => {
    if (!inEditMode) {
      setUpdatedDisplayName(undefined);
      setNewImage(undefined);
    } else {
      setUpdatedDisplayName(displayName);
    }
  }, [inEditMode, displayName]);


  const onClickEdit = async () => {
    let image;
    if (newImage) {
      image = newImage.split(',')[1];
    }
    const body = {
      displayName: updatedDisplayName,
      image
    };
    try {
      const response = await updateUser(username, body);
      setInEditMode(false);
      setUser(response.data)
    } catch (error) {

    }
  }

  const onChangeFile = (event) => {
    if (event.target.files.length < 1) {
      return;
    }
    const file = event.target.files[0];
    const fileReader = new FileReader();
    fileReader.onloadend = () => {
      setNewImage(fileReader.result);
    }
    fileReader.readAsDataURL(file);
  }


  const pendingApiCall = useApiProgress('put', '/api/users/' + username);


  let message = "We cannot edit";
  if (pathUsername === loggedInUsername) {
    message = "We can edit";
  }


  return (

    <div className="container">
      <br></br>
      <div class="main-body">
        <div className="row gutters-sm">
          <div className="col-md-4 mb-3">
            <div className="card">
              <div className="card-body">
                <div className="d-flex flex-column align-items-center text-center">
                  <ProfileImageWithDefault
                    className="rounded-circle shadow"
                    width="150"
                    height="160"
                    alt={`${username} profile`}
                    image={image}
                    temimage={newImage}
                  />
                  <div className="mt-3">
                    <h4>{username}</h4>
                    <p class="text-secondary mb-1">Bilgisayar Mühendisliği</p>
                    <button class="btn btn-primary">Follow</button>
                    <button class="btn btn-outline-primary">Message</button>
                  </div>
                </div>
              </div>
            </div>
            <div className="card mt-3">

              <ul className="list-group list-group-flush">
                <h5 class="mb-1 text-center">Kayıtlı Olduğu Topluluklar</h5>
                <li className="list-group-item d-flex justify-content-between align-items-center flex-wrap">
                  <p class="text-secondary mb-1">ACM SELCUK (Association for Computing Machinery )</p>
                  <p class="text-secondary mb-1">ACM KTUN (Association for Computing Machinery )</p>
                </li>
              </ul>
            </div>
          </div>
          <div class="col-md-8">
            <div class="card mb-3">
              <div class="card-body">
                <div class="row">
                  <div class="col-sm-3">
                    <h6 class="mb-0">Adı Soyadı</h6>
                  </div>
                  <div class="col-sm-9 text-secondary">
                    Veli Adin
                    </div>
                </div>
                <hr></hr>
                <div class="row">
                  <div class="col-sm-3">
                    <h6 class="mb-0">Email</h6>
                  </div>
                  <div class="col-sm-9 text-secondary">
                    adinveli@gmail.com
                    </div>
                </div>
                <hr></hr>
                <div class="row">
                  <div class="col-sm-3">
                    <h6 class="mb-0">Üniversite</h6>
                  </div>
                  <div class="col-sm-9 text-secondary">
                    Selçuk Üniversitesi
                    </div>
                </div>
                <hr></hr>
                <div class="row">
                  <div class="col-sm-3">
                    <h6 class="mb-0">Bölüm</h6>
                  </div>
                  <div class="col-sm-9 text-secondary">
                    Bilgisayar Mühendisliği
                    </div>
                </div>
                <hr></hr>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>


    /*
    <div className="card text-center">
      <div className="card-header">
        <ProfileImageWithDefault
          className="rounded-circle shadow"
          width="150"
          height="160"
          alt={`${username} profile`}
          image={image}
          temimage={newImage}
        />
      </div>
      <div className="card-body">
        {(!inEditMode &&
          <>
            <h4>
              {displayName}
              {username}
            </h4>

            {editable && <button
              className="btn btn-success  d-inline-flex"
              onClick={() => setInEditMode(true)}>
              <span className="material-icons">edit</span>
              {t('Edit')}</button>}
          </>
        )}
        {(inEditMode && (
          <div>
            <Input
              label={t('Change Display Name')}
              defaultValue={displayName}
              onChange={(event) => {
                setUpdatedDisplayName(event.target.value)
              }} />
            <input type="file" onChange={onChangeFile} />
            <div>
              <ButtonWithProgress
                className="btn btn-primary d-inline-flex"
                onClick={onClickEdit}
                disabled={pendingApiCall}
                pendingApiCall={pendingApiCall}
                text={
                  <>
                    <i className="material-icons">save</i>
                    {t('Save')}
                  </>
                }
              />


              <button
                className="btn btn-light d-inline-flex ml-1"
                onClick={() => setInEditMode(false)}
                disabled={pendingApiCall}>
                <i className="material-icons">close</i>
                {t('Cancel')}
              </button>
            </div>
          </div>
        ))}


      </div>

    </div>*/
  );
};

export default ProfileCard;
