import React from 'react';
import ProfileImageWithDefault from '../components/ProfileImageWithDefault';
import { Link } from 'react-router-dom';
import { format } from 'timeago.js'
import { useTranslation } from 'react-i18next';

const ActivityView = (props) => {
    const { activity } = props;
    const { user, content, timestamp, fileAttachment } = activity;
    const { username, image } = user;

    const { i18n } = useTranslation();

    const formatted = format(timestamp, i18n.language);

    return (
        <div className="card mb-3">
            <div className="row no-gutters">
                <div className="col-md-4">
                    {fileAttachment && (
                        <div>
                            <img className="img-fluid" src={'images/' + fileAttachment.name} alt="Activity İmage" />

                        </div>
                    )}
                    {!fileAttachment && (
                        <img className="img-fluid" src="http://placehold.it/250x350" alt="Card image cap" />
                    )}

                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h5 className="card-title"> {activity.id} - {activity.title}</h5>
                        <p className="card-text">Tehapy projesinin sunumu</p>
                        <a href="#" className="btn btn-primary">Daha Fazla Oku... &rarr;</a>
                    </div>
                </div>
            </div>
            <div className="card-footer text-muted d-flex">
                <ProfileImageWithDefault image={image} width="32" height="32" className="rounded-circle m-1" />

                <p className="flex-fill m-auto pl-2">
                    {formatted} tarihinde <Link to={`/user/${username}`} className="text-dark">
                        {username}
                    </Link> tarafından oluşturuldu.
                </p>
            </div>
        </div>
    );
};

export default ActivityView;
