import { createStore } from 'redux';
import authReducer from './authReducer';
import SecureLS from 'secure-ls';


const secureLs = new SecureLS();

const getStateFromStorage = () => {
    const appAuth = secureLs.get('app-auth');

    let stateInLocalStorage = {
        isLoggedIn: false,
        username: undefined,
        displayName: undefined,
        image: undefined,
        password: undefined
    }

    if (appAuth) {
        try {
            stateInLocalStorage = JSON.parse(appAuth)
        } catch (error) {

        }

    }
    return stateInLocalStorage;
};

const updateStateInStorage = newState => {
    localStorage.setItem('app-auth', JSON.stringify(newState))

};

const configureStore = () => {

    const store = createStore(authReducer, getStateFromStorage(), window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

    store.subscribe(() => {//store'da her hangi bir değişimde arkadak çalışacak
        updateStateInStorage(store.getState());
    });

    return store;
}

export default configureStore;
