import React from 'react';
import { signup } from '../api/apiCalls';
import Input from '../components/input'
import { withTranslation } from 'react-i18next';
import ButtonWithProgress from '../components/buttonWithProgres';
import { withApiProgress } from '../shared/ApiProgress';
import { connect } from 'react-redux';
import { signupHandler } from '../redux/authActions';


class UserSignupPage extends React.Component {

    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null,
        errors: {}
    };

    onChange = event => {
        const { name, value } = event.target;
        const errors = { ...this.state.errors }
        const { t } = this.props;
        errors[name] = undefined;
        if (name === 'password' || name === 'passwordRepeat') {
            if (name === 'password' && value !== this.state.passwordRepeat) {
                errors.passwordRepeat = t('Password mismatch');
            } else if (name === 'passwordRepeat' && value !== this.state.password) {
                errors.passwordRepeat = t('Password mismatch');
            }
            else {
                errors.passwordRepeat = undefined;
            }
        }
        this.setState({
            [name]: value,
            errors
        });
    };

    onClickSignup = async event => {
        event.preventDefault();

        const { history, dispatch } = this.props; //signuphandler
        const { push } = history; //signup handler
        const { username, displayName, password } = this.state;
        const body = {
            username,
            displayName,
            password
        }

        try {
            const response = await dispatch(signupHandler(body));
            push('/login');
        } catch (error) {
            if (error.response.data.validationErrors) {
                this.setState({ errors: error.response.data.validationErrors });
            }
        }

    };

    render() {
        const { errors } = this.state;
        const { username, displayName, password, passwordRepeat } = errors;
        const { t, pendingApiCall } = this.props;
        return (

            <div className="container">
                <form>
                    <h1 className="text-center">{t('Sign Up')}</h1>
                    <div className="form-group">
                        <Input name="username" label={t('Username')} error={username} onChange={this.onChange} />
                        <Input name="displayName" label={t('Display Name')} error={displayName} onChange={this.onChange} />
                        <Input name="password" label={t('Password')} error={password} onChange={this.onChange} type="password" />
                        <Input name="passwordRepeat" label={t('Password Repeat')} error={passwordRepeat} onChange={this.onChange} type="password" />
                        <ButtonWithProgress
                            onClick={this.onClickSignup}
                            disabled={pendingApiCall || passwordRepeat !== undefined}
                            pendingApiCall={pendingApiCall}
                            text={t('Sign Up')} />

                    </div>
                </form>
            </div>
        );
    }
}

const UserSignupPageWithApiProgress = withApiProgress(UserSignupPage, '/api/createuser');
const UserSignupPageWithTranslation = withTranslation()(UserSignupPageWithApiProgress);
export default connect()(UserSignupPageWithTranslation);
